package myjavacourse2020;

public class ForloopClass {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1; i<=20; i++) {
		
	System.out.print(i + " ");
	}
		System.out.println();
		for(int i =20; i>=1; i--) {
			System.out.print(i+ " ");
		}
		System.out.println();
		System.out.println("-------------------------------");
	
		
		int sum =0;
	for(int i =1; i<=10; i++){
	sum = sum+i;
	}
	System.out.println("sum of 1 to 10:" + sum);
	
	}
	
	}
	

	